package daily0321;

public class MathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.min(3, 13));	//최소값
		System.out.println(Math.max(123, 54));	//최대값
		System.out.println(Math.abs(-23.1));	//절대값
		System.out.println(Math.sqrt(3));		//제곱근
		System.out.println(Math.random());		//랜덤
	}

}

